
public class BuscarPrimeraLetra {

	
	/*
	 * Funciones
	 */
	
	public static char primeraLetra(String Palabra) {
		
		char letra =  Palabra.charAt(0);
		return letra;
	}
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		primeraLetra("Dame mi chapa");
	}

}


